import React, { useState } from "react";
import LandingPage from "./components/LandingPage";
import LoginPage from "./components/LoginPage";
import RegisterPage from "./components/RegisterPage";

function App() {
  const [currentPage, setCurrentPage] = useState("landing");

  const handleRegister = (userData) => {
    console.log("User registered:", userData);
    alert(`Welcome, ${userData.username}! Registration successful.`);
    setCurrentPage("login"); // redirect to login after register
  };

  const handleLogin = (userData) => {
    console.log("User logged in:", userData);
    alert(`Hello, ${userData.username}! You are logged in.`);
    setCurrentPage("landing"); // go back to landing after login
  };

  return (
    <div>
      {currentPage === "landing" && (
        <LandingPage onNavigate={setCurrentPage} />
      )}
      {currentPage === "login" && (
        <LoginPage onLogin={handleLogin} onNavigate={setCurrentPage} />
      )}
      {currentPage === "register" && (
        <RegisterPage onRegister={handleRegister} onNavigate={setCurrentPage} />
      )}
    </div>
  );
}

export default App;
