import React, { useState } from "react";

const RentPage = ({ onSubmit }) => {
  const [formData, setFormData] = useState({
    userType: "student",
    budget: "",
    roomType: "single",
    location: "",
    sharing: "no",
  });

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    onSubmit(formData); 
  };

  return (
    <div className="flex flex-col items-center justify-center min-h-screen bg-gray-100">
      <div className="bg-white shadow-lg rounded-2xl p-6 w-full max-w-md">
        <h2 className="text-2xl font-bold text-center mb-4 text-blue-600">
          Find Your Perfect Stay
        </h2>

        <form className="flex flex-col gap-4" onSubmit={handleSubmit}>
          {/* User Type */}
          <label className="font-semibold">You are a:</label>
          <select
            name="userType"
            value={formData.userType}
            onChange={handleChange}
            className="p-2 border rounded-lg"
          >
            <option value="student">Student</option>
            <option value="working">Working Professional</option>
          </select>

          {/* Budget */}
          <label className="font-semibold">Monthly Rent Range (â‚¹):</label>
          <input
            type="number"
            name="budget"
            placeholder="Enter max budget"
            value={formData.budget}
            onChange={handleChange}
            className="p-2 border rounded-lg"
          />

          {/* Room Type */}
          <label className="font-semibold">Room Type:</label>
          <select
            name="roomType"
            value={formData.roomType}
            onChange={handleChange}
            className="p-2 border rounded-lg"
          >
            <option value="single">Single Room</option>
            <option value="sharing">Sharing Room</option>
            <option value="flat">Flat</option>
            <option value="pg">PG / Hostel</option>
          </select>

          {/* Location */}
          <label className="font-semibold">Preferred Location:</label>
          <input
            type="text"
            name="location"
            placeholder="Enter city or area"
            value={formData.location}
            onChange={handleChange}
            className="p-2 border rounded-lg"
          />

          {/* Sharing */}
          <label className="font-semibold">Do you prefer sharing?</label>
          <select
            name="sharing"
            value={formData.sharing}
            onChange={handleChange}
            className="p-2 border rounded-lg"
          >
            <option value="no">No</option>
            <option value="yes">Yes</option>
          </select>

          {/* Submit Button */}
          <button
            type="submit"
            className="mt-4 bg-blue-600 text-white py-2 rounded-lg hover:bg-blue-700"
          >
            Submit
          </button>
        </form>
      </div>
    </div>
  );
};

export default RentPage;
