import React from "react";

export default function RentPage() {
  const container = {
    padding: "40px",
    fontFamily: "Arial, sans-serif",
    background: "#f9f9f9",
    minHeight: "100vh",
  };

  const card = {
    background: "white",
    padding: "20px",
    margin: "15px 0",
    borderRadius: "8px",
    boxShadow: "0 4px 8px rgba(0,0,0,0.1)",
  };

  const title = {
    color: "#2575fc",
  };

  return (
    <div style={container}>
      <h1 style={title}>Available Rentals</h1>
      <div style={card}>
        <h3>Luxury Car</h3>
        <p>Rent a premium car for your special events.</p>
      </div>
      <div style={card}>
        <h3>Professional Camera</h3>
        <p>High-quality cameras for your projects.</p>
      </div>
      <div style={card}>
        <h3>Banquet Hall</h3>
        <p>Spacious halls for weddings and events.</p>
      </div>
    </div>
  );
}
