import React, { useState } from "react";
import { useNavigate } from "react-router-dom";

/*
  PartTimeJobPage.jsx
  - Field list implemented:
    - preferredLocation (text)
    - preferredJobRole (dropdown of part-time roles)
    - preferredTime (dropdown + optional custom input)
    - salaryExpectation (number)
    - qualification (dropdown)
    - nationality (radio: Indian / Non-Indian). If Non-Indian -> country input
    - isStudent (radio). If yes -> college/university name + college ID number + upload college ID file
    - age (number)
    - comfortableWithNightShift (radio yes/no)
    - resume upload (optional)
*/

export default function PartTimeJobPage() {
  const navigate = useNavigate();

  const jobOptions = [
    "Tutor / Teaching Assistant",
    "Delivery Rider / Delivery Partner",
    "Retail Associate / Sales",
    "Data Entry / Backoffice",
    "Freelance Content Writer",
    "Graphic Designer (Freelance)",
    "Customer Support (Work from Home)",
    "Barista / Cafe Staff",
    "Event Staff / Helper",
    "Research Assistant (Part-time)",
  ];

  const qualificationOptions = [
    "High School (10+2)",
    "Diploma",
    "Bachelor's Degree",
    "Master's Degree",
    "PhD",
    "Other",
  ];

  const timeOptions = ["Morning (6am - 12pm)", "Afternoon (12pm - 5pm)", "Evening (5pm - 10pm)", "Night (10pm - 6am)", "Flexible"];

  const [form, setForm] = useState({
    preferredLocation: "",
    preferredJobRole: "",
    preferredTime: "",
    customPreferredTime: "",
    salaryExpectation: "",
    qualification: "",
    isIndian: "yes",
    country: "",
    isStudent: "no",
    collegeName: "",
    collegeIdNumber: "",
    collegeIdFile: null,
    age: "",
    comfortableNightShift: "no",
    resumeFile: null,
  });

  // For showing uploaded filenames
  const [fileNames, setFileNames] = useState({ collegeIdFile: null, resumeFile: null });

  const handleChange = (e) => {
    const { name, value, type, files } = e.target;
    if (type === "file") {
      const file = files && files[0] ? files[0] : null;
      setForm((p) => ({ ...p, [name]: file }));
      setFileNames((p) => ({ ...p, [name]: file ? file.name : null }));
    } else {
      setForm((p) => ({ ...p, [name]: value }));
    }
  };

  const validate = () => {
    // Basic front-end validation
    if (!form.preferredLocation.trim()) {
      alert("Please enter preferred location.");
      return false;
    }
    if (!form.preferredJobRole) {
      alert("Please select a preferred job role.");
      return false;
    }
    if (!form.preferredTime) {
      alert("Please select a preferred time.");
      return false;
    }
    if (!form.salaryExpectation || isNaN(Number(form.salaryExpectation))) {
      alert("Please enter a valid salary expectation (number).");
      return false;
    }
    if (!form.qualification) {
      alert("Please select your qualification.");
      return false;
    }
    if (form.isIndian === "no" && !form.country.trim()) {
      alert("Please specify your country of origin.");
      return false;
    }
    if (!form.age || isNaN(Number(form.age)) || Number(form.age) < 16) {
      alert("Please enter a valid age (must be 16 or older).");
      return false;
    }
    if (form.isStudent === "yes") {
      if (!form.collegeName.trim()) {
        alert("Please enter your college/university name.");
        return false;
      }
      if (!form.collegeIdNumber.trim()) {
        alert("Please enter your college/university ID number.");
        return false;
      }
      // collegeIdFile optional but recommended
    }
    return true;
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    if (!validate()) return;

    // Prepare a serializable object to send to results page (files can't be serialized fully)
    const sendData = {
      ...form,
      // replace files with file names only (we can upload later to backend)
      collegeIdFileName: form.collegeIdFile ? form.collegeIdFile.name : null,
      resumeFileName: form.resumeFile ? form.resumeFile.name : null,
    };

    console.log("Part-time job preferences submitted:", sendData);

    // Navigate to results page passing data
    navigate("/jobs-results", { state: { data: sendData } });
  };

  const container = {
    display: "flex",
    justifyContent: "center",
    alignItems: "flex-start",
    minHeight: "100vh",
    background: "#f7f9fc",
    padding: "40px 20px",
    fontFamily: "Arial, sans-serif",
  };

  const card = {
    background: "#ffffff",
    padding: "26px",
    borderRadius: "12px",
    boxShadow: "0 6px 18px rgba(20,30,50,0.08)",
    width: "760px",
    maxWidth: "100%",
  };

  const sectionTitle = { fontSize: "20px", marginBottom: "12px", color: "#12263a" };
  const label = { display: "block", marginBottom: "6px", fontSize: "14px", color: "#334e68" };
  const row = { display: "flex", gap: "12px", alignItems: "center", marginBottom: "12px" };
  const input = { flex: 1, padding: "10px 12px", borderRadius: "8px", border: "1px solid #d6dde6", fontSize: "14px" };
  const select = { ...input };
  const smallInput = { padding: "8px 10px", borderRadius: "8px", border: "1px solid #d6dde6", fontSize: "14px", width: "180px" };
  const fileInput = { marginTop: "6px", marginBottom: "10px" };
  const submitBtn = { padding: "12px 18px", background: "#0b6bff", color: "#fff", border: "none", borderRadius: "8px", cursor: "pointer", fontWeight: "600", marginTop: "10px" };

  return (
    <div style={container}>
      <div style={card}>
        <h2 style={sectionTitle}>Find Part-Time Jobs — Preferences</h2>

        <form onSubmit={handleSubmit}>
          {/* Preferred location */}
          <div style={{ marginBottom: "14px" }}>
            <label style={label}>Preferred Location</label>
            <input
              type="text"
              name="preferredLocation"
              placeholder="City, area or neighbourhood (e.g., New Delhi, Koramangala)"
              value={form.preferredLocation}
              onChange={handleChange}
              style={input}
              required
            />
          </div>

          {/* Preferred Job Role + Preferred Time + Salary */}
          <div style={row}>
            <div style={{ flex: 1 }}>
              <label style={label}>Preferred Job Role</label>
              <select name="preferredJobRole" value={form.preferredJobRole} onChange={handleChange} style={select} required>
                <option value="">— choose a part-time role —</option>
                {jobOptions.map((j) => (
                  <option key={j} value={j}>{j}</option>
                ))}
              </select>
            </div>

            <div style={{ width: "260px" }}>
              <label style={label}>Preferred Time</label>
              <select name="preferredTime" value={form.preferredTime} onChange={handleChange} style={smallInput} required>
                <option value="">— time slot —</option>
                {timeOptions.map((t) => (
                  <option key={t} value={t}>{t}</option>
                ))}
                <option value="custom">Custom</option>
              </select>

              {form.preferredTime === "custom" && (
                <input
                  type="text"
                  name="customPreferredTime"
                  placeholder="e.g., Mon-Fri 6pm-9pm"
                  value={form.customPreferredTime}
                  onChange={handleChange}
                  style={{ ...smallInput, marginTop: "8px", width: "100%" }}
                />
              )}
            </div>

            <div style={{ width: "170px" }}>
              <label style={label}>Salary Expectation (₹ / month)</label>
              <input
                type="number"
                name="salaryExpectation"
                placeholder="e.g., 8000"
                value={form.salaryExpectation}
                onChange={handleChange}
                style={smallInput}
                required
                min="0"
              />
            </div>
          </div>

          {/* Qualification, Nationality, Age */}
          <div style={row}>
            <div style={{ width: "260px" }}>
              <label style={label}>Qualification</label>
              <select name="qualification" value={form.qualification} onChange={handleChange} style={smallInput} required>
                <option value="">— select —</option>
                {qualificationOptions.map((q) => (
                  <option key={q} value={q}>{q}</option>
                ))}
              </select>
            </div>

            <div style={{ width: "320px" }}>
              <label style={label}>Nationality</label>
              <div style={{ display: "flex", gap: "12px", alignItems: "center" }}>
                <label style={{ fontSize: "14px" }}>
                  <input type="radio" name="isIndian" value="yes" checked={form.isIndian === "yes"} onChange={handleChange} /> Indian
                </label>
                <label style={{ fontSize: "14px" }}>
                  <input type="radio" name="isIndian" value="no" checked={form.isIndian === "no"} onChange={handleChange} /> Not Indian
                </label>
                {form.isIndian === "no" && (
                  <input type="text" name="country" placeholder="Country" value={form.country} onChange={handleChange} style={{ ...smallInput, width: "170px" }} />
                )}
              </div>
            </div>

            <div style={{ width: "140px" }}>
              <label style={label}>Age</label>
              <input type="number" name="age" min="10" value={form.age} onChange={handleChange} style={smallInput} required />
            </div>
          </div>

          {/* Student, college details */}
          <div style={{ marginBottom: "12px" }}>
            <label style={label}>Are you a student?</label>
            <div style={{ display: "flex", gap: "12px", alignItems: "center" }}>
              <label>
                <input type="radio" name="isStudent" value="yes" checked={form.isStudent === "yes"} onChange={handleChange} /> Yes
              </label>
              <label>
                <input type="radio" name="isStudent" value="no" checked={form.isStudent === "no"} onChange={handleChange} /> No
              </label>
            </div>

            {form.isStudent === "yes" && (
              <div style={{ marginTop: "10px" }}>
                <label style={label}>College / University Name</label>
                <input type="text" name="collegeName" value={form.collegeName} onChange={handleChange} style={input} required />
                <label style={{ ...label, marginTop: "8px" }}>College / University ID Number</label>
                <input type="text" name="collegeIdNumber" value={form.collegeIdNumber} onChange={handleChange} style={input} required />
                <label style={{ ...label, marginTop: "8px" }}>Upload College ID (optional)</label>
                <input type="file" name="collegeIdFile" accept=".jpg,.jpeg,.png,.pdf" onChange={handleChange} style={fileInput} />
                {fileNames.collegeIdFile && <div style={{ fontSize: "13px", color: "#666" }}>Selected: {fileNames.collegeIdFile}</div>}
              </div>
            )}
          </div>

          {/* Night shift */}
          <div style={{ marginBottom: "12px" }}>
            <label style={label}>Are you comfortable with night shift work?</label>
            <div style={{ display: "flex", gap: "12px", alignItems: "center" }}>
              <label><input type="radio" name="comfortableNightShift" value="yes" checked={form.comfortableNightShift === "yes"} onChange={handleChange} /> Yes</label>
              <label><input type="radio" name="comfortableNightShift" value="no" checked={form.comfortableNightShift === "no"} onChange={handleChange} /> No</label>
            </div>
          </div>

          {/* Resume upload */}
          <div style={{ marginBottom: "12px" }}>
            <label style={label}>Upload Resume (optional)</label>
            <input type="file" name="resumeFile" accept=".pdf,.doc,.docx,.jpg,.png" onChange={handleChange} style={fileInput} />
            {fileNames.resumeFile && <div style={{ fontSize: "13px", color: "#666" }}>Selected: {fileNames.resumeFile}</div>}
          </div>

          <div style={{ display: "flex", justifyContent: "flex-end" }}>
            <button type="submit" style={submitBtn}>Submit Preferences</button>
          </div>
        </form>
      </div>
    </div>
  );
}
