import React, { useState } from "react";

export default function Register() {
  const [role, setRole] = useState(""); // "student" or "landlord"

  const handleRoleChange = (e) => setRole(e.target.value);

  return (
    <div className="flex justify-center items-center min-h-screen bg-gray-100">
      <div className="bg-white shadow-lg rounded-2xl p-8 w-full max-w-md">
        <h2 className="text-2xl font-bold mb-6 text-gray-800 text-center">
          Register
        </h2>

        {/* --- Role Selection --- */}
        <div className="mb-6 text-center">
          <label className="font-semibold text-gray-700 mr-4">Register as:</label>
          <label className="mr-4">
            <input
              type="radio"
              value="student"
              checked={role === "student"}
              onChange={handleRoleChange}
              className="mr-2"
            />
            Student
          </label>
          <label>
            <input
              type="radio"
              value="landlord"
              checked={role === "landlord"}
              onChange={handleRoleChange}
              className="mr-2"
            />
            Property Owner
          </label>
        </div>

        {/* --- Conditional Form Display --- */}
        {!role ? (
          <p className="text-gray-500 text-center">
            Please select a role to continue registration.
          </p>
        ) : (
          <form className="space-y-4">
            <div>
              <label className="block font-medium text-gray-700">Name</label>
              <input
                type="text"
                placeholder="Enter full name"
                className="w-full border border-gray-300 rounded-lg p-2 focus:outline-none focus:ring focus:ring-blue-300"
              />
            </div>

            <div>
              <label className="block font-medium text-gray-700">Email</label>
              <input
                type="email"
                placeholder="Enter your email"
                className="w-full border border-gray-300 rounded-lg p-2 focus:outline-none focus:ring focus:ring-blue-300"
              />
            </div>

            <div>
              <label className="block font-medium text-gray-700">Password</label>
              <input
                type="password"
                placeholder="Enter a password"
                className="w-full border border-gray-300 rounded-lg p-2 focus:outline-none focus:ring focus:ring-blue-300"
              />
            </div>

            {/* Student-Specific Fields */}
            {role === "student" && (
              <>
                <div>
                  <label className="block font-medium text-gray-700">
                    University Name
                  </label>
                  <input
                    type="text"
                    placeholder="Enter university name"
                    className="w-full border border-gray-300 rounded-lg p-2 focus:outline-none focus:ring focus:ring-blue-300"
                  />
                </div>

                <div>
                  <label className="block font-medium text-gray-700">
                    Upload Student ID (image or PDF)
                  </label>
                  <input
                    type="file"
                    accept="image/*,application/pdf"
                    className="w-full border border-gray-300 rounded-lg p-2"
                  />
                </div>
              </>
            )}

            {/* Landlord-Specific Fields */}
            {role === "landlord" && (
              <>
                <div>
                  <label className="block font-medium text-gray-700">
                    Property Name
                  </label>
                  <input
                    type="text"
                    placeholder="Enter property name"
                    className="w-full border border-gray-300 rounded-lg p-2 focus:outline-none focus:ring focus:ring-blue-300"
                  />
                </div>

                <div>
                  <label className="block font-medium text-gray-700">
                    Upload Property Ownership Proof (image or PDF)
                  </label>
                  <input
                    type="file"
                    accept="image/*,application/pdf"
                    className="w-full border border-gray-300 rounded-lg p-2"
                  />
                </div>
              </>
            )}

            <button
              type="submit"
              className="w-full bg-blue-600 text-white font-semibold rounded-lg py-2 hover:bg-blue-700 transition"
            >
              Register
            </button>

            <button
              type="button"
              className="w-full border border-blue-600 text-blue-600 font-semibold rounded-lg py-2 hover:bg-blue-50 transition"
            >
              Go to Login
            </button>
          </form>
        )}
      </div>
    </div>
  );
}
