import React, { useState } from "react";
import { motion, AnimatePresence } from "framer-motion";

export default function Register() {
  const [role, setRole] = useState("");
  const [step, setStep] = useState(1);

  const handleRoleSelection = (selectedRole) => {
    setRole(selectedRole);
    setStep(2);
  };

  const handleBack = () => {
    setStep(1);
    setRole("");
  };

  return (
    <div className="flex justify-center items-center min-h-screen bg-gradient-to-br from-blue-50 to-indigo-100">
      <AnimatePresence mode="wait">
        {step === 1 && (
          <motion.div
            key="step1"
            initial={{ opacity: 0, y: 40 }}
            animate={{ opacity: 1, y: 0 }}
            exit={{ opacity: 0, y: -40 }}
            transition={{ duration: 0.5 }}
            className="bg-white p-10 rounded-2xl shadow-xl w-full max-w-md text-center"
          >
            <h2 className="text-3xl font-bold text-gray-800 mb-6">
              Register As
            </h2>
            <p className="text-gray-600 mb-8">
              Choose your role to continue registration
            </p>

            <div className="space-y-4">
              <button
                onClick={() => handleRoleSelection("student")}
                className="w-full bg-blue-600 text-white py-3 rounded-lg font-semibold hover:bg-blue-700 transition"
              >
                🎓 Student
              </button>
              <button
                onClick={() => handleRoleSelection("landlord")}
                className="w-full bg-green-600 text-white py-3 rounded-lg font-semibold hover:bg-green-700 transition"
              >
                🏠 Property Owner
              </button>
            </div>
          </motion.div>
        )}

        {step === 2 && (
          <motion.div
            key="step2"
            initial={{ opacity: 0, y: 40 }}
            animate={{ opacity: 1, y: 0 }}
            exit={{ opacity: 0, y: -40 }}
            transition={{ duration: 0.5 }}
            className="bg-white p-10 rounded-2xl shadow-xl w-full max-w-md"
          >
            <h2 className="text-2xl font-bold mb-6 text-gray-800 text-center">
              {role === "student"
                ? "Student Registration"
                : "Property Owner Registration"}
            </h2>

            <form className="space-y-4">
              <div>
                <label className="block text-gray-700 font-medium">Name</label>
                <input
                  type="text"
                  placeholder="Enter full name"
                  className="w-full border border-gray-300 rounded-lg p-2 focus:ring focus:ring-blue-200"
                />
              </div>

              <div>
                <label className="block text-gray-700 font-medium">Email</label>
                <input
                  type="email"
                  placeholder="Enter your email"
                  className="w-full border border-gray-300 rounded-lg p-2 focus:ring focus:ring-blue-200"
                />
              </div>

              <div>
                <label className="block text-gray-700 font-medium">
                  Password
                </label>
                <input
                  type="password"
                  placeholder="Enter a password"
                  className="w-full border border-gray-300 rounded-lg p-2 focus:ring focus:ring-blue-200"
                />
              </div>

              {role === "student" && (
                <>
                  <div>
                    <label className="block text-gray-700 font-medium">
                      University Name
                    </label>
                    <input
                      type="text"
                      placeholder="Enter university name"
                      className="w-full border border-gray-300 rounded-lg p-2 focus:ring focus:ring-blue-200"
                    />
                  </div>

                  <div>
                    <label className="block text-gray-700 font-medium">
                      Upload Student ID (image or PDF)
                    </label>
                    <input
                      type="file"
                      accept="image/*,application/pdf"
                      className="w-full border border-gray-300 rounded-lg p-2"
                    />
                  </div>
                </>
              )}

              {role === "landlord" && (
                <>
                  <div>
                    <label className="block text-gray-700 font-medium">
                      Property Name
                    </label>
                    <input
                      type="text"
                      placeholder="Enter property name"
                      className="w-full border border-gray-300 rounded-lg p-2 focus:ring focus:ring-green-200"
                    />
                  </div>

                  <div>
                    <label className="block text-gray-700 font-medium">
                      Upload Ownership Proof (image or PDF)
                    </label>
                    <input
                      type="file"
                      accept="image/*,application/pdf"
                      className="w-full border border-gray-300 rounded-lg p-2"
                    />
                  </div>
                </>
              )}

              <div className="flex justify-between mt-6">
                <button
                  type="button"
                  onClick={handleBack}
                  className="border border-gray-400 text-gray-700 px-4 py-2 rounded-lg hover:bg-gray-100 transition"
                >
                  ← Back
                </button>

                <button
                  type="submit"
                  className={`${
                    role === "student" ? "bg-blue-600" : "bg-green-600"
                  } text-white px-6 py-2 rounded-lg hover:opacity-90 transition`}
                >
                  Register
                </button>
              </div>
            </form>
          </motion.div>
        )}
      </AnimatePresence>
    </div>
  );
}
